package main

import (
	"errors"
	"testing"
)

func TestValid(t *testing.T) {
	qr := "NZCP:"

	_, err := NewToken(qr)
	if err != nil {
		t.Errorf("Valid token did not validate: %v", err)
	}
}

var invalidQRs = []struct {
	desc     string
	expected error
	qr       string
}{
	{
		desc:     "bad signature 1 (signed by different key)",
		expected: ErrBadSignature,
		qr:       "NZCP:",
	},
	{
		desc:     "unknown public key",
		expected: ErrUnknownPublicKey,
		qr:       "NZCP:",
	},
	{
		desc:     "bad signature 2 (modified signature)",
		expected: ErrBadSignature,
		qr:       "NZCP:",
	},
	{
		desc:     "bad signature 3 (modified payload)",
		expected: ErrBadSignature,
		qr:       "NZCP:",
	},
	{
		desc:     "expired token",
		expected: ErrTokenExpired,
		qr:       "NZCP:",
	},
	{
		desc:     "inactive token",
		expected: ErrTokenNotActive,
		qr:       "NZCP:",
	},
	{
		desc:     "empty",
		expected: ErrMissingNZCPPrefix,
		qr:       "",
	},
	{
		desc:     "missing prefix",
		expected: ErrMissingNZCPPrefix,
		qr:       "boop/1/data",
	},
	{
		desc:     "missing version",
		expected: ErrBadNZCPVersion,
		qr:       "NZCP://data",
	},
	{
		desc:     "invalid version",
		expected: ErrBadNZCPVersion,
		qr:       "NZCP:/999/data",
	},
	{
		desc:     "bad data",
		expected: ErrBadNZCPPayload,
		qr:       "NZCP:/1/$%^&*",
	},
	{
		desc:     "incomplete data",
		expected: ErrInvalidTokenFormat,
		qr:       "NZCP:/1/2KCEV",
	},
}

func TestInvalid(t *testing.T) {
	for _, s := range invalidQRs {
		t.Run(s.desc, func(t *testing.T) {
			_, err := NewToken(s.qr)
			if !errors.Is(err, s.expected) {
				t.Errorf("Expected %v, got %v", s.expected, err)
			}
		})
	}
}
